<?php

namespace AppBundle\Repository;

use AppBundle\Entity\OrderedProducts;
use AppBundle\Entity\Product;
use AppBundle\Entity\User;
use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query\Expr\Join;

/**
 * OrderedProductsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class OrderedProductsRepository extends EntityRepository implements IOrderedProductsRepository
{
    /**
     * @param User $user
     * @return array
     */
    public function getOrderedProductsByUser(User $user): array
    {
        $query = $this->getEntityManager()
                      ->getRepository(OrderedProducts::class)
                      ->createQueryBuilder('op')
                      ->select('op.id as orderedProductID, pr.title AS Product, op.orderedDate, pr.id AS viewProductID,
                                      op.quantity AS Quantity, op.orderedProductPrice AS Price')
                      ->join('op.user','u')
                      ->join('op.product', 'pr')
                      ->where('u = :user')
                      ->andWhere('op.quantity > 0')
                      ->andWhere('pr.outOfStock = 0')
                      ->setParameter(':user', $user)
                      ->getQuery();

        return $query->getArrayResult();
    }

    /**
     * @param User $user
     * @param Product $product
     * @return bool
     * @throws \Exception
     */
    public function addOrderedProduct(User $user, Product $product): bool
    {
        $em = $this->getEntityManager();
        $em->getUnitOfWork()->scheduleForUpdate($user);

        $orderedProduct = new OrderedProducts();

        $orderedProduct->setUser($user);
        $orderedProduct->setOrderedDate(new \DateTime('now'));
        $orderedProduct->setConfirmed(false);
        $orderedProduct->setQuantity(1);
        $orderedProduct->setProduct($product);
        $orderedProduct->setOrderedProductPrice($product->getPrice());

        $product->setQuantity($product->getQuantity() - 1);

        $em->persist($orderedProduct);

        if (true === $em->getUnitOfWork()->isScheduledForInsert($orderedProduct)
            && true === $em->getUnitOfWork()->isScheduledForUpdate($user)) {
                $user->setTotalCheck($user->getTotalCheck() + $product->getPrice());
                $em->flush();
                return true;
        }

        return false;
    }

    /**
     * @param Product $product
     * @return OrderedProducts|null|object
     */
    public function findOrderedProductByProduct(Product $product)
    {
        return  $this->getEntityManager()
                     ->getRepository(OrderedProducts::class)
                     ->findOneBy([
                         'product' => $product
                     ]);
    }

    /**
     * @param OrderedProducts $orderedProducts
     * @param User $user
     * @param Product $product
     * @return bool
     */
    public function removeOrderedProduct(OrderedProducts $orderedProducts,
                                         User $user,
                                         Product $product): bool
    {
        $em = $this->getEntityManager();

        $em->getUnitOfWork()->scheduleForUpdate($user);
        $em->getUnitOfWork()->scheduleForUpdate($product);

        $orderedProducts->setQuantity($orderedProducts->getQuantity() - 1);
        $orderedProducts->setOrderedProductPrice(0);

        if ($orderedProducts->getConfirmed() < 1) {
            $em->remove($orderedProducts);
        }

        $user->setTotalCheck($user->getTotalCheck() - $product->getPrice());

        $product->setQuantity($product->getQuantity() + 1);

        if (true === $em->getUnitOfWork()->isScheduledForUpdate($user)
            && true === $em->getUnitOfWork()->isScheduledForUpdate($product))
        {
                $em->flush();
                return true;
        }

        return false;
    }

    /**
     * @param OrderedProducts $orderedProduct
     * @param Product $product
     * @return bool
     */
    public function increaseQuantity(OrderedProducts $orderedProduct, Product $product): bool
    {
        $em = $this->getEntityManager();

        $em->getUnitOfWork()->scheduleForUpdate($orderedProduct);
        $user = $orderedProduct->getUser();

        $user->setTotalCheck($user->getTotalCheck() + $product->getPrice());

        $quantity      = $orderedProduct->getQuantity() + 1;
        $increasePrice = $orderedProduct->getOrderedProductPrice() + $product->getPrice();

        $orderedProduct->setQuantity($quantity);
        $orderedProduct->setOrderedProductPrice($increasePrice);
        $orderedProduct->setOrderedDate(new \DateTime('now'));

        $product->setQuantity($product->getQuantity() - 1);

        $em->getUnitOfWork()->scheduleForUpdate($orderedProduct);

        if (true === $em->getUnitOfWork()->isScheduledForUpdate($orderedProduct)) {
            $em->flush();
            return true;
        }

        return false;
    }

    /**
     * @param User $user
     * @param Product $product
     * @return OrderedProducts|null|object
     */
    public function findOrderedProductFromUserByID(User $user, Product $product)
    {
        return $this->getEntityManager()
                    ->getRepository(OrderedProducts::class)
                    ->findOneBy([
                        'user'      => $user,
                        'product'   => $product
                    ]);
    }

    public function findOrderedProductByID(int $id)
    {
        return $this->getEntityManager()
                     ->getRepository(OrderedProducts::class)
                     ->findOneBy([
                         'id' => $id
                     ]);
    }

    /**
     * @param OrderedProducts $orderedProduct
     * @param Product $product
     * @param User $user
     * @return bool
     */
    public function decreaseQuantity(OrderedProducts $orderedProduct,
                                     Product $product,
                                     User $user): bool
    {
        $em = $this->getEntityManager();

        $em->getUnitOfWork()->scheduleForUpdate($orderedProduct);
        $em->getUnitOfWork()->scheduleForUpdate($user);
        $em->getUnitOfWork()->scheduleForUpdate($product);

        $orderedProduct->setQuantity($orderedProduct->getQuantity() - 1);
        $orderedProduct->setOrderedProductPrice($orderedProduct->getOrderedProductPrice() - $product->getPrice());

        $product->setQuantity($product->getQuantity() + 1);

        $user->setTotalCheck($user->getTotalCheck() - $product->getPrice());

        if (true === $em->getUnitOfWork()->isScheduledForUpdate($orderedProduct) &&
            true === $em->getUnitOfWork()->isScheduledForUpdate($user)
            && true === $em->getUnitOfWork()->isScheduledForUpdate($product))
        {
            $em->flush();
            return true;
        }

        return false;
    }

    /**
     * @param User $user
     * @return float
     */
    public function getCheckoutFromAllProducts(User $user): float
    {
        $query = $this->getEntityManager()
                      ->getRepository(OrderedProducts::class)
                      ->createQueryBuilder('op')
                      ->select('SUM (op.orderedProductPrice)')
                      ->where('op.user = :user')
                      ->andWhere('op.quantity > 0')
                      ->setParameters([
                          ':user' => $user
                      ]);

        return $query->getQuery()
                     ->getSingleScalarResult();
    }

    /**
     * @param Product $product
     * @param User $user
     * @return float
     */
    public function getCheckoutFromSpecificProduct(Product $product, User $user): float
    {
        $query = $this->getEntityManager()
                      ->getRepository(OrderedProducts::class)
                      ->createQueryBuilder('op')
                      ->select('op.orderedProductPrice')
                      ->where('op.user = :user')
                      ->andWhere('op.product = :product')
                      ->setParameters([
                          ':user'   => $user,
                          ':product' => $product
                      ]);

        return $query->getQuery()->getSingleScalarResult();
    }

    /**
     * @param User $user
     * @return array
     */
    public function getAllBoughtProductsByUser(User $user): array
    {
        $query = $this->getEntityManager()
                      ->getRepository(OrderedProducts::class)
                      ->createQueryBuilder('op')
                      ->select('op.id as orderedProductID, product.title AS Product, op.orderedDate, op.confirmed, user.username AS Seller,
                                      product.id AS productID, op.quantity AS Quantity')
                      ->innerJoin('op.product', 'product', Join::WITH)
                      ->innerJoin('op.user', 'user', Join::WITH)
                      ->where('op.confirmed <> 0')
                      ->andWhere('op.user = :user')
                      ->setParameters([
                          ':user' => $user
                      ]);

        return $query->getQuery()->getResult();
    }

    /**
     * @param Product $product
     * @param OrderedProducts $orderedProducts
     * @param int $currentProductQuantity
     * @return bool
     */
    public function sellBoughtProduct(Product $product,
                                      OrderedProducts $orderedProducts,
                                      int $currentProductQuantity): bool
    {
        $em = $this->getEntityManager();
        $em->getUnitOfWork()->scheduleForUpdate($product);
        $em->getUnitOfWork()->scheduleForUpdate($orderedProducts);

        $orderedProducts->setConfirmed($orderedProducts->getConfirmed() - $product->getQuantity());

        $product->setOutOfStock(false);
        $product->setQuantity($product->getQuantity() + $currentProductQuantity);

        if (true === $em->getUnitOfWork()->isScheduledForUpdate($orderedProducts)
            && true === $em->getUnitOfWork()->isScheduledForUpdate($product)) {
                $em->flush();
                return true;
        }

        return false;
    }

    public function deleteOrderedProduct(OrderedProducts $orderedProducts): bool
    {
        $em = $this->getEntityManager();
        $em->remove($orderedProducts);

        if (true === $em->getUnitOfWork()->isScheduledForDelete($orderedProducts)) {
            $em->flush();
            return true;
        }

        return false;
    }

    /**
     * @param Product $product
     * @param User $user
     * @return null|OrderedProducts
     */
    public function getOrderedProductByProductAndUser(Product $product,
                                                      User $user)
    {
        return $this->getEntityManager()
                    ->getRepository(OrderedProducts::class)
                    ->findOneBy([
                        'product' => $product,
                        'user'    => $user
                    ]);
    }

    /**
     * @param OrderedProducts $orderedProducts
     * @return bool
     */
    public function updateBoughtProductOnSameUser(OrderedProducts $orderedProducts): bool
    {
        $em = $this->getEntityManager();
        $em->getUnitOfWork()->scheduleForUpdate($orderedProducts);

        if (true === $em->getUnitOfWork()->isEntityScheduled($orderedProducts)) {
            $em->flush();
            return true;
        }

        return false;
    }

    /**
     * @param User $changedUser
     * @param OrderedProducts $orderedProducts
     * @return bool
     */
    public function updateBoughtProductOnChangedUser(User $changedUser,
                                                     OrderedProducts $orderedProducts): bool
    {
        $em = $this->getEntityManager();
    }

    /**
     * @param User $changedUser
     * @param OrderedProducts $orderedProducts
     * @return bool
     */
    public function attachBoughtProductToUserWithoutContextOrder(User $changedUser,
                                                                 OrderedProducts $orderedProducts): bool
    {
        $em    = $this->getEntityManager();

        $order = new OrderedProducts();
        $order->setQuantity($orderedProducts->getQuantity());
        $order->setConfirmed($orderedProducts->getConfirmed());
        $order->setOrderedDate($orderedProducts->getOrderedDate());
        $order->setUser($orderedProducts->getUser());
        $order->setOrderedProductPrice(0);
        $order->setProduct($orderedProducts->getProduct());

        $em->remove($orderedProducts);
        $em->persist($order);

        if (true === $em->getUnitOfWork()->isScheduledForInsert($order)
            && true === $em->getUnitOfWork()->isScheduledForDelete($orderedProducts)) {
                $em->flush();
                return true;
        }

        return false;
    }
}
